import { AUTH, LOGOUT } from '../constant/actionTypes'

const authReducer = (state = { authData : null }, action) => {
    switch (action.type) {
        case AUTH:
            console.log(action?.payload)    
            return state
        default:
            return state
    }
}

export default authReducer
