import React,{ useState } from "react";
import { Container, Grow, Grid, Paper, TextField, Button, AppBar } from '@material-ui/core'
import { useNavigate, useLocation } from "react-router-dom"

import Posts from '../Posts/Posts'
import Form from "../Form/Form";
import Pagination from "../Pagination";
import useStyles from './styles'

function useQuery(){
    return new URLSearchParams(useLocation().search) // setup url search params for to know which page currently on and what's search looking for 
}

const Home = ({ dispatch, currentId, setCurrentId, user }) => {
    const classes = useStyles()
    const query = useQuery()    // bring page info form
    const navigate = useNavigate()
    const page = query.get('page') || 1 // read url and see what we have a parameter in there
    const searchQuery = query.get('searchQuery') 
    const [search, setSearch] = useState('')

    console.log(page)
    console.log(searchQuery)

    // CHIP
    const [onChangeTag, setOnChangeTag] = useState("");
    const [tags, setTags] = useState([]);
    const tagRef = useRef(null);


    function handleKeyPress(e) {
        if(e.keyCode === 13){ //e.keyCode === 13 its mean the ENTER keypress

        }
    }

    return (
        <Grow in >
            <Container maxWidth='xl'>
                <Grid container justifyContent='space-between' alignItems='stretch' spacing={3} className={classes.gridContainer}>
                    <Grid item xs={12} sm={6} md={9}>
                        <Posts setCurrentId={setCurrentId} />
                    </Grid>
                    <Grid item xs={12} sm={6} md={3}>
                        <AppBar className={classes.appBarSearch}  position='static' color='inherit'>
                        <TextField 
                        name='search' 
                        label='Search Moment'
                        variant="outlined"
                        fullWidth
                        value={search}
                        onChange={(e) => setSearch(e.target.value)}
                        onKeyPress={handleKeyPress}
                        />
                        <TextField
                        name="Search Tags"
                        label="Search Tags"
                        variant="outlined"
                        fullWidth
                        style={{
                            margin: "10px 0 0",
                            overflow: "auto",
                        }}
                        inputRef={tagRef}
                        InputProps={{
                            startAdornment:
                                tags.length !== 0 &&
                                tags.map((tag) => (
                                    <Chip
                                        key={tag}
                                        style={{ margin: "10px 0" }}
                                        onDelete={() =>
                                            handleDelete(tag)
                                        }
                                        label={tag}
                                        variant="outlined"
                                    />
                                )),
                            onChange: (event) => {
                                setOnChangeTag(event.target.value);
                            },
                            onKeyPress: (event) => {
                                if (event.key === "Enter") {
                                    setTags([...tags, onChangeTag]);
                                    setOnChangeTag("");
                                    tagRef.current.value = "";
                                }
                            },
                        }}
                        />
                        </AppBar>
                        <Form currentId={currentId} dispatch={dispatch} setCurrentId={setCurrentId} user={user} />
                        <Paper elevation={6}>
                            <Pagination />
                        </Paper>
                    </Grid>
                </Grid>
            </Container>
        </Grow>
    )
}

export default Home
